import React, { useState } from 'react'
import Filter from './components/Filter'
import Form from './components/Form'

const App = () => {
  const [Persons, setPersons] = useState([
    { name: "Bashir Salisu Sidi", number: "08067753836" },
    { name: "Abba Ali", number: "07034284523" },
    { name: "Baba Ali", number: "08067239374" },
    { name: "Umar Baba", number: "07054743838" }
  ])
  const [NewName, setNewName] = useState('')
  const [Number, setNumber] = useState('')
  const [FilterValue, setFilterValue] = useState('')

  const nameC = Persons.find(p => p.name.toLowerCase() === NewName.toLowerCase());

  const filteredPersons = Persons.filter(p =>
    (p.name.toLowerCase().indexOf(FilterValue.toLowerCase()) > 0) ||
    (p.number.toLowerCase().indexOf(FilterValue.toLowerCase()) > 0)
  )

  console.log("filteredPersons :", filteredPersons)
  const handleNameChange = (event) => {
    setNewName(event.target.value);
  }
  const handleNumberChange = (event) => {
    setNumber(event.target.value)
  }
  const handleFilterValueChange = (event) => {
    setFilterValue(event.target.value)
  }

  const addName = (event) => {
    event.preventDefault();
    const name = {
      name: NewName,
      number: Number
    }

    if (nameC !== undefined) {
      alert(`${NewName} is already added to phonebook.`)
      setNumber('');
      setNewName('');
    } else {
      setPersons(Persons.concat(name));
      setNewName('')
      setNumber('')
    }
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <div>
        <Filter
          inputType="text"
          filterString={FilterValue}
          handleFilterValueChange={handleFilterValueChange}
        />
      </div>
      <div>
        <Form
          iType="text"
          handleNameChange={handleNameChange}
          handleNumberChange={handleNumberChange}
          Number={Number}
          Name={NewName}
          handleSubmit={addName}
        />
      </div>
      <h2>Numbers</h2>
      {Persons.map((p, i) => (
        <div key={i}>
          <table>
            <tbody>
              <tr>
                <td>{p.name}:</td>
                <td>{p.number}</td>
              </tr>
            </tbody>
          </table>
        </div>
      ))}
    </div>
  )
}

export default App
