import React, { useState } from 'react'

const App = () => {
  const [Persons, setPersons] = useState([
    { name: "Bashir Salisu Sidi", number: "08067753836" },
    { name: "Abba Ali", number: "07034284523" },
    { name: "Baba Ali", number: "08067239374" },
    { name: "Umar Baba", number: "07054743838" }
  ])
  const [NewName, setNewName] = useState('')
  const [Number, setNumber] = useState('')
  const [FilterValue, setFilterValue] = useState('')

  const nameC = Persons.find(p => p.name.toUpperCase() === NewName.toUpperCase());
  const filteredPersons = Persons.filter(p =>
    (p.name.toUpperCase() === FilterValue.toUpperCase()) ||
    (p.number.toUpperCase() === FilterValue.toUpperCase())
  )

  console.log("filteredPersons :", filteredPersons)
  console.log("Query", FilterValue)
  const handleNameChange = (event) => {
    setNewName(event.target.value);
  }
  const handleNumberChange = (event) => {
    setNumber(event.target.value)
  }
  const handleFilterValueChange = (event) => {
    console.log("filteredPersons :", filteredPersons)
    setFilterValue(event.target.value)
  }

  const addName = (event) => {
    event.preventDefault();
    const name = {
      name: NewName,
      number: Number
    }

    if (nameC !== undefined) {
      alert(`${NewName} is already added to phonebook.`)
      setNumber('');
      setNewName('');
    } else {
      setPersons(Persons.concat(name));
      setNewName('')
      setNumber('')
    }
  }

  return (
    <div>
      <h2>Phonebook</h2>

      <div>
        filter shown with
        <input
          type="text"
          value={FilterValue}
          onChange={handleFilterValueChange}
        />
      </div>


      <h2>Add new</h2>
      <form onSubmit={addName}>
        <table>
          <tbody>
            <tr>
              <td>Fullname:</td>
              <td>
                <input
                  type="text"
                  value={NewName}
                  onChange={handleNameChange}
                />
              </td>
            </tr>
            <tr>
              <td>Number:</td>
              <td>
                <input
                  type="text"
                  value={Number}
                  onChange={handleNumberChange}
                />
              </td>
            </tr>
          </tbody>
        </table>
        <button type="submit">Add</button>
      </form>
      <h2>Numbers</h2>
      {Persons.map((p, i) => (
        <div key={i}>
          <table>
            <tbody>
              <tr>
                <td>{p.name}:</td>
                <td>{p.number}</td>
              </tr>
            </tbody>
          </table>
        </div>
      ))}
    </div>
  )
}

export default App
